const App = () => {
  const [selected, setSelected] = useState('aws');

  return (
    <div className="p-6 space-y-4">
      <SelectCard
        label="Select Server Provider"
        required
        options={serverProviders}
        onChange={(item) => setSelected(item.key)}
        selectedKey={selected}
        renderItem={(item) => (
          <div className="w-20 flex flex-col items-center justify-between gap-4">
            <div className="w-12">{item.icon}</div>
            <h4 className="block text-xs font-medium text-gray-900">{item.title}</h4>
          </div>
        )}
      />
    </div>
  );
};

export default App;
